javascript:(function(){var embeddedBody='<script src="https://cdnjs.cloudflare.com/ajax/libs/jquery/2.1.4/jquery.min.js" type="text/javascript" charset="utf-8"></script>\n<div id="color-palette">\n    <style type="text/css" media="screen">\n        #color-palette {\n            position: fixed;\n            top: 15%;\n            left: 25%;\n            background-color: white;\n            padding: 20px;\n            border-color: gray;\n            border-style: dashed;\n            border-width: 3px;\n            width: 50%;\n            z-index: 100000;\n            color: black;\n            font-size: 15px;\n        }\n        #close-palette {\n            position: absolute;\n            right: 25px;\n            top: 20px;\n            width: 20px;\n            cursor: pointer;\n            text-align: center;\n        }\n        .color {\n            display: inline-block;\n            width: 20%;\n            margin: 5px;\n            padding: 5px;\n            text-align: center;\n        }\n        .color-swatch {\n            display: inline-block;\n            width: 100%;\n            height: 50px;\n            border-style: solid;\n            border-width: 1px;\n        }\n        #color-title {\n            text-align: center;\n        }\n        #color-footer {\n            float: right;\n        }\n        .highlight {\n            border-style: solid;\n            border-width: 3px;\n            border-color: red;\n        }\n    </style>\n\n    <div id="color-title">Website Color Palette Generator</div>\n    <div id="close-palette">X</div>\n    <div id="palettes">\n\n    </div>\n    <div id="color-footer">Copyright Codi Marker</div>\n\n</div>',embeddedScript="var elementTypes = ['div', 'body', 'html', 'a', 'li', 'h1',  'h2', 'h3', 'h4', 'h5', 'h6', 'span', 'nav'];\n\nvar colors = [];\nvar highlighted = [];\n\nfunction hex(x) {\n    return (\"0\" + parseInt(x).toString(16)).slice(-2);\n}\n\nfunction addColor(color) {\n    var hexColor = color.match(/^rgb\\((\\d+),\\s*(\\d+),\\s*(\\d+)\\)$/);\n    if (!hexColor)\n        hexColor = color.match(/^rgba\\((\\d+),\\s*(\\d+),\\s*(\\d+),\\s*(\\d+)\\)$/);\n    if (!hexColor) {\n        console.log(\"bad color: \" + color);\n        return;\n    }\n    if (hexColor[4] != null && hexColor[4] == 0)\n        return;\n    if (colors.indexOf(color) == -1) {\n        colors.push(color);\n    }\n}\n\nwindow.jQuery( document ).ready(function() {\n    //debugger;\n    function getColors() {\n        elementTypes.forEach(function(elementType) {\n            //debugger;\n            window.jQuery(elementType).each(function(idx, element) {\n                //debugger;\n                //console.log(element);\n                if (!window.jQuery(element).css)\n                    return;\n                var color = window.jQuery(element).css('background-color');\n                addColor(color);\n            });\n        });\n    }\n    function getElementsOfColor(color) {\n        var ret = [];\n        elementTypes.forEach(function(elementType) {\n            //debugger;\n            window.jQuery(elementType).each(function(idx, element) {\n                //debugger;\n                //console.log(element);\n                if (!window.jQuery(element).css)\n                    return;\n                var elementColor = window.jQuery(element).css('background-color');\n                if (elementColor == color)\n                    ret.push(element);\n            });\n        });\n        return ret;\n    }\n    function removeHighlights() {\n        highlighted.forEach(function(element) {\n            window.jQuery(element).removeClass(\"highlight\");\n        });\n    }\n    getColors();\n\n    window.jQuery.each(colors, function(index,color) {\n        //debugger;\n        var hexColor = color.match(/^rgb\\((\\d+),\\s*(\\d+),\\s*(\\d+)\\)$/);\n        if (!hexColor){\n            hexColor = color.match(/^rgba\\((\\d+),\\s*(\\d+),\\s*(\\d+),\\s*(\\d+)\\)$/);\n            if (!hexColor)\n                return;\n        }\n        hexColor = \"#\" + hex(hexColor[1]) + hex(hexColor[2]) + hex(hexColor[3]);\n        var html = \"<div class='color'><div class='color-swatch' style='background-color: \" + color + \"'></div><div>\" + hexColor + \"</div></div>\"; // '<li><h3 class=\"name\">'+userName+'</h3></li>'\n        window.jQuery('#palettes').append(html);\n    });\n\n    window.jQuery('.color').mouseover(function(){\n        //debugger;\n        var newColor = $(this).children().eq(0).css(\"background-color\");\n        removeHighlights();\n        highlighted = [];\n        highlighted = getElementsOfColor(newColor);\n        highlighted.forEach(function(element) {\n            window.jQuery(element).addClass(\"highlight\");\n        });\n    });\n\n    window.jQuery('.color').click(function(){\n        if (highlighted.length) {\n            $('html, body').animate({\n                scrollTop: $(highlighted[0]).offset().top\n            }, 500);\n        }\n    });\n\n    window.jQuery('#close-palette').click(function(){\n        console.log(\"Closing\");\n        window.jQuery('#palette-inject').remove();\n        removeHighlights();\n    });\n});",newhtml='<div id="palette-inject"><script>'+embeddedScript+"</script> "+embeddedBody+"</div>";window.jQuery("body").append(newhtml);})();